<template>  <div class="register-container">    <div class="register-card">      <div class="register-header">        <div class="logo-container">          <img            src="@/assets/logo.png"            alt="Career Seekers Logo"            class="logo"          >        </div>        <h1 class="register-title">          Регистрация родителя        </h1>        <!-- Индикатор шагов -->        <div class="steps-indicator">          <div            class="step"            :class="{ active: currentStep >= 1, completed: currentStep > 1 }"          >            <div class="step-number">              1            </div>            <div class="step-label">              Данные родителя            </div>          </div>          <div            class="step"            :class="{ active: currentStep >= 2, completed: currentStep > 2 }"          >            <div class="step-number">              2            </div>            <div class="step-label">              Данные ребенка            </div>          </div>        </div>        <div class="divider" />      </div>      <div class="register-content">        <form          class="register-form"          @submit.prevent="handleSubmit"        >          <!-- Шаг 1: Данные родителя -->          <div            v-if="currentStep === 1"            class="step-content"          >            <h3 class="step-title">              Данные родителя            </h3>            <div class="field">              <label                for="lastName"                class="field-label"              >Фамилия *</label>              <InputText                id="lastName"                v-model="parentForm.lastName"                placeholder="Введите вашу полную фамилию"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.lastName }"              />              <small                v-if="parentFormErrors.lastName"                class="p-error"              >{{ parentFormErrors.lastName }}</small>            </div>            <div class="field">              <label                for="firstName"                class="field-label"              >Имя *</label>              <InputText                id="firstName"                v-model="parentForm.firstName"                placeholder="Введите ваше полное имя"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.firstName }"              />              <small                v-if="parentFormErrors.firstName"                class="p-error"              >{{ parentFormErrors.firstName }}</small>            </div>            <div class="field">              <label                for="patronymic"                class="field-label"              >Отчество</label>              <InputText                id="patronymic"                v-model="parentForm.patronymic"                placeholder="Введите ваше полное отчество"                class="w-full"              />            </div>            <div class="field">              <label                for="birthDate"                class="field-label"              >Дата рождения *</label>              <InputMask                id="birthDate"                v-model="parentForm.birthDate"                mask="99.99.9999"                placeholder="дд.мм.гггг"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.birthDate }"              />              <small                v-if="parentFormErrors.birthDate"                class="p-error"              >{{ parentFormErrors.birthDate }}</small>            </div>            <div class="field">              <label                for="relationship"                class="field-label"              >Кем приходится? *</label>              <InputText                id="relationship"                v-model="parentForm.relationship"                placeholder="Например: мать, отец, опекун"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.relationship }"              />              <small                v-if="parentFormErrors.relationship"                class="p-error"              >{{ parentFormErrors.relationship }}</small>            </div>            <div class="field">              <label                for="telegramLink"                class="field-label"              >Ссылка на Telegram *</label>              <InputText                id="telegramLink"                v-model="parentForm.telegramLink"                placeholder="Например, @telegram_username"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.telegramLink }"                @blur="validateTelegramLink"              />              <small                v-if="parentFormErrors.telegramLink"                class="p-error"              >{{ parentFormErrors.telegramLink }}</small>            </div>            <div class="field">              <label                for="phone"                class="field-label"              >Контактный телефон *</label>              <InputMask                id="phone"                v-model="parentForm.phone"                mask="+7 (999) 999-99-99"                placeholder="+7 (___) ___-__-__"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.phone }"              />              <small                v-if="parentFormErrors.phone"                class="p-error"              >{{ parentFormErrors.phone }}</small>            </div>            <div class="field">              <label                for="email"                class="field-label"              >Адрес электронной почты *</label>              <InputText                id="email"                v-model="parentForm.email"                type="email"                placeholder="example@email.com"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.email }"                @blur="validateEmail"              />              <small                v-if="parentFormErrors.email"                class="p-error"              >{{ parentFormErrors.email }}</small>            </div>            <div class="field">              <label                for="password"                class="field-label"              >Пароль *</label>              <Password                id="password"                v-model="parentForm.password"                placeholder="Введите пароль"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.password }"                :feedback="true"                toggle-mask                weak-label="Слабый"                medium-label="Средний"                strong-label="Надежный"                prompt-label="Введите пароль"                @blur="validatePassword"              />              <small                v-if="parentFormErrors.password"                class="p-error"              >{{ parentFormErrors.password }}</small>            </div>            <div class="field">              <label                for="confirmPassword"                class="field-label"              >Подтверждение пароля *</label>              <Password                id="confirmPassword"                v-model="parentForm.confirmPassword"                placeholder="Подтвердите пароль"                class="w-full"                :class="{ 'p-invalid': parentFormErrors.confirmPassword }"                :feedback="false"                toggle-mask              />              <small                v-if="parentFormErrors.confirmPassword"                class="p-error"              >{{ parentFormErrors.confirmPassword }}</small>            </div>            <div class="field">              <div class="flex align-items-center">                <Checkbox                  v-model="parentForm.isParentMentor"                  input-id="isParentMentor"                  :binary="true"                />                <label                  for="isParentMentor"                  class="ml-2 agreement-label"                >                  Я (родитель) являюсь наставником                </label>              </div>            </div>            <div class="field">              <div class="flex align-items-center">                <Checkbox                  v-model="parentForm.consent"                  input-id="consent"                  :binary="true"                  :class="{ 'p-invalid': parentFormErrors.consent }"                />                <label                  for="consent"                  class="ml-2 agreement-label"                >                  Я согласен(-а) на                  <a                  href="#"                  class="link"                  @click.prevent="showAgreement"                >                    обработку персональных данных                  </a>                </label>              </div>              <small                v-if="parentFormErrors.consent"                class="p-error"              >{{ parentFormErrors.consent }}</small>            </div>            <div class="field">              <div class="flex align-items-center">                <Checkbox                  v-model="parentForm.agreement"                  input-id="agreement"                  :binary="true"                  :class="{ 'p-invalid': parentFormErrors.agreement }"                />                <label                  for="agreement"                  class="ml-2 agreement-label"                >                  Я согласен с                  <a                  href="#"                  class="link"                  @click.prevent="showPolitics"                >политикой использования сервиса</a>                </label>              </div>              <small                v-if="parentFormErrors.agreement"                class="p-error"              >{{ parentFormErrors.agreement }}</small>            </div>            <div class="field">              <div class="flex align-items-center">                <Checkbox                  v-model="parentForm.rules"                  input-id="rules"                  :binary="true"                  :class="{ 'p-invalid': parentFormErrors.rules }"                />                <label                  for="rules"                  class="ml-2 agreement-label"                >                  с                  <a                  href="#"                  class="link"                  @click.prevent="showRules"                >Положением</a>                  ознакомлен                </label>              </div>              <small                v-if="parentFormErrors.rules"                class="p-error"              >{{ parentFormErrors.rules }}</small>            </div>          </div>          <!-- Шаг 2: Данные ребенка -->          <div            v-if="currentStep === 2"            class="step-content"          >            <h3 class="step-title">              Данные ребенка            </h3>            <AddChildForm              :model-child-form="childForm"              :model-child-form-errors="childFormErrors"              @update:model-child-form="(form) => childForm = form"              @update:model-child-form-errors="(formErrors) => childFormErrors = formErrors"              @update:home-education="(val) => isHomeEducated = val"              @update:home-preparation="(val) => isHomePrepared = val"            />          </div>          <!-- Кнопки навигации -->          <div class="step-navigation">            <Button              v-if="currentStep > 1"              type="button"              label="Назад"              class="p-button-secondary"              @click="previousStep"            />            <Button              v-if="currentStep < 2"              type="button"              label="Далее"              class="p-button-primary"              @click="nextStep"            />            <Button              v-if="currentStep === 2"              type="submit"              label="Зарегистрироваться"              class="p-button-primary"              :loading="isLoading"            />          </div>          <div class="login-link">            <p>              Уже есть аккаунт?              <router-link                to="/login"                class="link"              >                Войти              </router-link>            </p>          </div>        </form>      </div>    </div>    <!-- Диалоги -->    <Dialog      v-model:visible="showAgreementDialog"      modal      :show-header="false"      :style="{ width: '90vw', maxWidth: '650px', height: '90vh', position: 'relative', borderRadius: '6px', }"      class="privacy-dialog"    >      <div style="          display: flex;          position: absolute;          left: 0;          top: 0;          height: 90vh;          width: 100%;          overflow: hidden;          background-color: white;          border-radius: 6px;        ">        <VuePdfEmbed          style="width: 100%; position: absolute"          source="/docs/agreement_user.pdf"        />        <Button          style="            position: absolute;            top: 0.5rem;            padding: 1rem;            right: 0.5rem;            width: auto;            height: 5%;          "          icon="pi pi-times"          class="p-button-text p-button-plain"          aria-label="Close"          @click="showAgreementDialog = false"        />      </div>    </Dialog>    <!-- Диалог политики конфиденциальности -->    <Dialog      v-model:visible="showPoliticsDialog"      modal      :show-header="false"      :style="{ width: '90vw', maxWidth: '650px', height: '90vh', position: 'relative', borderRadius: '6px', }"      class="privacy-dialog"    >      <div style="          position: absolute;          padding: 3rem 0;          left: 0;          height: 90vh;          width: 100%;          background-color: white;          border-radius: 6px;        ">        <div style="height: 100%; overflow: scroll">          <VuePdfEmbed source="/docs/politics.pdf" />        </div>        <Button          style="            position: fixed;            top: 0.5rem;            padding: 1rem;            right: 0.5rem;            width: auto;            height: 5%;          "          icon="pi pi-times"          class="p-button-text p-button-plain"          aria-label="Close"          @click="showPoliticsDialog = false"        />      </div>    </Dialog>    <Dialog      v-model:visible="showRulesDialog"      modal      :show-header="false"      :style="{ width: '90vw', maxWidth: '650px', height: '90vh', position: 'relative', borderRadius: '6px', }"      class="privacy-dialog"    >      <div style="          display: flex;          position: absolute;          left: 0;          top: 0;          height: 90vh;          width: 100%;          overflow: hidden;          background-color: white;          border-radius: 6px;        ">        <iframe          title="PDF Document"          width="100%"          height="100%"          :src="rulesLink"        />        <Button          style="            position: absolute;            top: 0.5rem;            padding: 1rem;            right: 0.5rem;            width: auto;            height: 5%;          "          icon="pi pi-times"          class="p-button-text p-button-plain"          aria-label="Close"          @click="showRulesDialog = false"        />      </div>    </Dialog>    <ToastPopup :content="toastPopupErrors" />  </div></template><script lang="ts">import InputText from "primevue/inputtext";import InputMask from "primevue/inputmask";import Password from "primevue/password";import Button from "primevue/button";import Checkbox from "primevue/checkbox";import Dialog from "primevue/dialog";import { AuthResolver } from "@/api/resolvers/auth/auth.resolver";import ToastPopup from "@/components/ToastPopup.vue";import type { UserWithChildRegistrationDto } from "@/api/resolvers/auth/dto/input/register-input.dto.ts";import {  type RegistrationData, Roles, type UserCachedData,} from '@/state/UserState.types';import { FileManager } from "@/utils/FileManager";import VuePdfEmbed from "vue-pdf-embed";import AddChildForm, { type ChildFormErrors, type ChildFormFields } from '@/components/AddChildForm.vue';import { useGradeOptions } from '@/shared/UseGradeOptions.ts';import { FormatManager } from '@/utils/FormatManager.ts';export type ParentFormErrors = {  lastName: string,  firstName: string,  patronymic: string,  birthDate: string;  relationship: string;  phone: string;  telegramLink: string;  email: string;  password: string;  confirmPassword: string;  childConsentFile: string;  consent: string;  agreement: string;  rules: string;}export default {  name: "ParentRegisterView",  components: {    AddChildForm,    VuePdfEmbed,    ToastPopup,    InputText,    InputMask,    Password,    Button,    Checkbox,    Dialog,  },  data() {    return {      currentStep: 1 as number,      isLoading: false,      showAgreementDialog: false,      showPoliticsDialog: false,      showRulesDialog: false,      isHomeEducated: false,      isHomePrepared: false,      toastPopupErrors: {        title: "",        message: "",      },      parentForm: {        lastName: "",        firstName: "",        patronymic: null,        birthDate: "",        relationship: "",        telegramLink: "",        phone: "",        email: "",        password: "",        confirmPassword: "",        isParentMentor: false,        consent: false,        agreement: false,        rules: false,        childConsentFile: null as null | File,      },      childForm: {        lastName: "",        firstName: "",        patronymic: null,        birthDate: "",        snilsNumber: "",        schoolName: "",        grade: null,        platform: "",        childConsentFile: null as null | File,        birthCertificate: null as null | File,        snilsScan: null as null | File,        schoolCertificate: null as null | File,        platformCertificate: null as null | File,      } as ChildFormFields,            childFormErrors: {        lastName: '',        firstName: '',        birthCertificate: '',        birthDate: '',        grade: '',        platform: '',        childConsentFile: '',        platformCertificate: '',        schoolCertificate: '',        schoolName: '',        snilsNumber: '',        snilsScan: ''      } as ChildFormErrors,            parentFormErrors: {        lastName: "",        firstName: "",        patronymic: "",        birthDate: "",        relationship: "",        phone: "",        telegramLink: "",        email: "",        password: "",        confirmPassword: "",        consent: "",        agreement: "",        rules: ""      }  as ParentFormErrors,      gradeOptions: useGradeOptions,      platformOptions: [        { label: "Площадка 1", value: "platform1" },        { label: "Площадка 2", value: "platform2" },        { label: "Площадка 3", value: "platform3" },      ],    };  },  computed: {    telegramLinkFormatted() {      return this.parentForm.telegramLink.replace("@", "https://t.me/");    },    rulesLink() {      return "/public/docs/rules.pdf#toolbar=0&#navpanes=0&#view=Fit"    }  },  methods: {    validateEmail() {      if (        this.parentForm.email &&        !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(this.parentForm.email)      ) {        this.parentFormErrors.email = "Введите корректный email";      } else {        this.parentFormErrors.email = "";      }    },    validatePassword() {      if (!this.parentForm.password) {        this.parentFormErrors.password = "Пароль обязателен";        return;      } else if (this.parentForm.password.length < 8) {        this.parentFormErrors.password = "Пароль должен содержать минимум 8 символов";        return;      } else if (        !/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(this.parentForm.password)      ) {        this.parentFormErrors.password =          "Пароль должен содержать заглавные и строчные буквы, а также цифры";        return;      }      this.parentFormErrors.password = "";    },    validateStep(step: number) {      Object.keys(this.parentFormErrors).forEach(key => {        this.parentFormErrors[key as keyof ParentFormErrors] = "";      });      Object.keys(this.childFormErrors).forEach(key => {        this.childFormErrors[key as keyof ChildFormErrors] = "";      });      let isValid = true;      if (step === 1) {        // Валидация данных родителя        if (!this.parentForm.lastName.trim()) {          this.parentFormErrors.lastName = "Фамилия обязательна";          isValid = false;        }        if (!this.parentForm.firstName.trim()) {          this.parentFormErrors.firstName = "Имя обязательно";          isValid = false;        }        if (!this.parentForm.relationship.trim()) {          this.parentFormErrors.relationship = "Укажите, кем вы приходитесь ребенку";          isValid = false;        }        if (!this.parentForm.telegramLink.trim()) {          this.parentFormErrors.telegramLink = "Ссылка обязательна для связи";          isValid = false;        } else if (          !/^@[a-zA-Z][a-zA-Z0-9_]{4,31}$/.test(this.parentForm.telegramLink)        ) {          this.parentFormErrors.telegramLink = "Введите корректную ссылку";          isValid = false;        }        if (!this.parentForm.birthDate.trim()) {          this.parentFormErrors.birthDate = "Дата рождения обязательна";          isValid = false;        }        if (!this.parentForm.phone) {          this.parentFormErrors.phone = "Телефон обязателен";          isValid = false;        } else if (this.parentForm.phone.replace(/\D/g, "").length !== 11) {          this.parentFormErrors.phone = "Введите корректный номер телефона";          isValid = false;        }        if (!this.parentForm.email) {          this.parentFormErrors.email = "Email обязателен";          isValid = false;        } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(this.parentForm.email)) {          this.parentFormErrors.email = "Введите корректный email";          isValid = false;        }        if (!this.parentForm.password) {          this.parentFormErrors.password = "Пароль обязателен";          isValid = false;        } else if (this.parentForm.password.length < 8) {          this.parentFormErrors.password = "Пароль должен содержать минимум 8 символов";          isValid = false;        }        if (!this.parentForm.confirmPassword) {          this.parentFormErrors.confirmPassword = "Подтверждение пароля обязательно";          isValid = false;        } else if (          this.parentForm.password !== this.parentForm.confirmPassword        ) {          this.parentFormErrors.confirmPassword = "Пароли не совпадают";          isValid = false;        }        // Проверка согласия с условиями использования        if (!this.parentForm.agreement) {          this.parentFormErrors.agreement =            "Необходимо согласиться с политикой использования сервиса";          isValid = false;        }        // Проверка согласия на обработку данных        if (!this.parentForm.consent) {          this.parentFormErrors.consent =            "Необходимо согласиться с обработкой персональных данных";          isValid = false;        }        // Проверка согласия с положением        if (!this.parentForm.rules) {          this.parentFormErrors.rules =            "Необходимо ознакомиться с Положением";          isValid = false;        }      }      if (step === 2) {        // Валидация данных ребенка        if (!this.childForm.lastName.trim()) {          this.childFormErrors.lastName = "Фамилия ребенка обязательна";          isValid = false;        }        if (!this.childForm.firstName.trim()) {          this.childFormErrors.firstName = "Имя ребенка обязательно";          isValid = false;        }        if (!this.childForm.birthDate) {          this.childFormErrors.birthDate = "Дата рождения обязательна";          isValid = false;        } else if (!/^\d{2}\.\d{2}\.\d{4}$/.test(this.childForm.birthDate)) {          this.childFormErrors.birthDate = "Введите дату в формате дд.мм.гггг";          isValid = false;        }        if (!this.childForm.birthCertificate) {          this.childFormErrors.birthCertificate =            "Необходимо загрузить скан свидетельства о рождении";          isValid = false;        }        if (!this.childForm.snilsNumber) {          this.childFormErrors.snilsNumber = "Номер СНИЛС обязателен";          isValid = false;        }        if (!this.childForm.snilsScan) {          this.childFormErrors.snilsScan = "Необходимо загрузить скан СНИЛС";          isValid = false;        }        if (!this.childForm.schoolName && !this.isHomeEducated) {          this.childFormErrors.schoolName = "Название учреждения обязательно";          isValid = false;        }        if (this.childForm.grade === null && !this.isHomeEducated) {          this.childFormErrors.grade = "Класс обучения обязателен";          isValid = false;        }        if (!this.childForm.platform && !this.isHomePrepared) {          this.childFormErrors.platform = "Выберите площадку подготовки";          isValid = false;        }        if (!this.childForm.schoolCertificate && !this.isHomeEducated) {          this.childFormErrors.schoolCertificate = "Необходимо загрузить справку из ОУ";          isValid = false;        }        if (!this.childForm.platformCertificate && !this.isHomePrepared) {          this.childFormErrors.platformCertificate =            "Необходимо загрузить справку из площадки подготовки";          isValid = false;        }        if (!this.childForm.childConsentFile) {          this.childFormErrors.childConsentFile =            "Необходимо загрузить согласие на обработку персональных данных";          isValid = false;        }      }      return isValid;    },    nextStep() {      if (this.validateStep(this.currentStep)) {        this.currentStep++;      }    },    previousStep() {      if (confirm("Все загруженные файлы будут сброшены, продолжить?"))        this.currentStep--;    },    validateTelegramLink() {      if (        this.parentForm.telegramLink &&        !/^@[a-zA-Z][a-zA-Z0-9_]{4,31}$/.test(this.parentForm.telegramLink)      ) {        this.parentFormErrors.telegramLink = "Введите корректную ссылку";      } else this.parentFormErrors.telegramLink = "";    },    handleSubmit: async function () {      if (!this.validateStep(2)) return;      this.isLoading = true;      try {        const authResolver = new AuthResolver();        const response = await authResolver.preRegister({          email: this.parentForm.email.toLowerCase(),          mobileNumber: this.parentForm.phone,        });        if (response.status !== 200) {          this.toastPopupErrors = {            title: `Ошибка #${response.status}`,            message: response.message,          };        } else {          const fileManager = new FileManager();          const registrationData: RegistrationData<            UserWithChildRegistrationDto,            UserCachedData          > = {            dto: {              type: "UserWithChildRegistrationDto",              verificationCode: "",              lastName: this.parentForm.lastName,              firstName: this.parentForm.firstName,              patronymic: this.parentForm.patronymic,              dateOfBirth: FormatManager.formatBirthDateToDTO(this.parentForm.birthDate),              email: this.parentForm.email.toLowerCase(),              mobileNumber: FormatManager.formatMobileNumberToDTO(this.parentForm.phone),              password: this.parentForm.password,              role: Roles.USER,              uuid: "",              mentorEqualsUser: this.parentForm.isParentMentor,              childLastName: this.childForm.lastName,              childFirstName: this.childForm.firstName,              childPatronymic: this.childForm.patronymic,              childDateOfBirth: FormatManager.formatBirthDateToDTO(this.childForm.birthDate),              mentorId: null            },            extra: {              snilsNumber: FormatManager.formatSnilsToDTO(this.childForm.snilsNumber),              snilsFileName: await fileManager.saveFileToCache(                this.childForm.snilsScan as File,              ),              studyingPlace: this.isHomeEducated                ? "Домашнее обучение"                : this.childForm.schoolName,              studyingCertificateFileName: await fileManager.saveFileToCache(                this.isHomeEducated                  ? await this.getHomeFile("home_education.txt")                  : this.childForm.schoolCertificate as File,              ),              learningClass: this.isHomeEducated                ? 0                : this.childForm.grade ?? 0,              trainingGround: this.isHomePrepared                ? "Домашнее обучение"                : this.childForm.platform as string,              additionalStudyingCertificateFileName:                await fileManager.saveFileToCache(                  this.isHomePrepared                    ? await this.getHomeFile("home_preparation.txt")                    : this.childForm.platformCertificate as File,                ),              parentRole: this.parentForm.relationship,              consentToChildPdpFilename: await fileManager.saveFileToCache(                this.childForm.childConsentFile as File,              ),              birthCertificateFilename: await fileManager.saveFileToCache(                this.childForm.birthCertificate as File,              ),            },          };          localStorage.setItem(            "dataToVerify",            JSON.stringify(registrationData),          );          localStorage.setItem(            "telegramLink",            JSON.stringify(this.telegramLinkFormatted),          );          this.$router.push({            path: "/email-confirmation",            query: { email: this.parentForm.email.toLowerCase() },          });        }      } catch (error) {        console.error("Ошибка регистрации:", error);      } finally {        this.isLoading = false;      }    },    async getHomeFile(filename: string) {      const response = await fetch(`/docs/${filename}`)      const blob = await response.blob()      return new File([], filename, { type: blob.type })    },    showAgreement() {      this.showAgreementDialog = true;    },    showPolitics() {      this.showPoliticsDialog = true;    },    showRules() {      this.showRulesDialog = true;    },  },};</script><style scoped>.register-container {  min-height: 100vh;  display: flex;  align-items: center;  justify-content: center;  padding: 1rem;}.register-card {  background: white;  border-radius: 12px;  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);  width: 100%;  max-width: 600px;  max-height: 90vh;  overflow: hidden;  position: relative;  display: flex;  flex-direction: column;  animation: slideUp 0.8s ease-out 0.2s both;}@keyframes slideUp {  from {    opacity: 0;    transform: translateY(30px);  }  to {    opacity: 1;    transform: translateY(0);  }}.register-header {  text-align: center;  padding: 2rem 2rem 0 2rem;  flex-shrink: 0;  background: white;  border-radius: 12px 12px 0 0;}.divider {  height: 1px;  background: linear-gradient(    90deg,    transparent 0%,    #e0e0e0 20%,    #e0e0e0 80%,    transparent 100%  );  margin: 1.5rem 0 0 0;}.register-content {  flex: 1;  overflow-y: auto;  padding: 0 2rem 2rem 2rem;  scrollbar-width: thin;  scrollbar-color: #ff9800 #f1f1f1;}.logo-container {  margin-bottom: 1.5rem;}.logo {  max-width: 120px;  height: auto;  object-fit: contain;}.register-title {  color: #2c3e50;  margin: 0 0 0.5rem 0;  font-size: 1.75rem;  font-weight: 600;  font-family: "BIPS", sans-serif;}/* Индикатор шагов */.steps-indicator {  display: flex;  justify-content: center;  margin: 1.5rem 0;  gap: 1.5rem;}.step {  display: flex;  flex-direction: column;  align-items: center;  gap: 0.5rem;  opacity: 0.5;  transition: opacity 0.3s ease;}.step.active,.step.completed {  opacity: 1;}.step-number {  width: 32px;  height: 32px;  border-radius: 50%;  background: #e0e0e0;  color: #666;  display: flex;  align-items: center;  justify-content: center;  font-weight: 600;  font-size: 0.9rem;  transition: all 0.3s ease;}.step.active .step-number {  background: #ff9800;  color: white;}.step.completed .step-number {  background: #4caf50;  color: white;}.step-label {  font-size: 0.75rem;  color: #666;  text-align: center;  margin-top: 0.25rem;}.step.active .step-label,.step.completed .step-label {  color: #2c3e50;  font-weight: 500;}.step-content {  animation: fadeInUp 0.6s ease-out;}@keyframes fadeInUp {  from {    opacity: 0;    transform: translateY(20px);  }  to {    opacity: 1;    transform: translateY(0);  }}.step-title {  color: #2c3e50;  margin: 0 0 1.5rem 0;  font-size: 1.2rem;  font-weight: 600;}.register-form {  display: flex;  flex-direction: column;  gap: 1.5rem;}.field {  display: flex;  flex-direction: column;  gap: 0.5rem;}.field-label {  font-weight: 500;  color: #2c3e50;  font-size: 0.9rem;}.step-navigation {  display: flex;  gap: 1rem;  margin-top: 2rem;}.step-navigation .p-button {  flex: 1;  justify-content: center;}/* Мобильные стили для кнопок навигации */@media (max-width: 768px) {  .step-navigation {    flex-direction: column;    gap: 0.75rem;  }  .step-navigation .p-button {    width: 100%;    flex: none;  }}.login-link {  text-align: center;  margin-top: 1rem;}.login-link p {  margin: 0;  color: #6c757d;}.agreement-label {  font-size: 0.9rem;  color: #2c3e50;  line-height: 1.4;}.link {  color: #007bff;  text-decoration: none;  font-weight: 500;}.link:hover {  text-decoration: underline;}:deep(.p-password) {  width: 100%;}:deep(.p-password-input) {  width: 100%;}:deep(.p-fileupload) {  width: 100%;}/* Стили для FileUpload на мобильных устройствах */@media (max-width: 768px) {  :deep(.p-fileupload .p-button) {    width: 100%;    text-align: center;    overflow: hidden;    text-overflow: ellipsis;    white-space: nowrap;    padding: 0.75rem 1rem;  }  :deep(.p-fileupload .p-button-label) {    max-width: 100%;    overflow: hidden;    text-overflow: ellipsis;    white-space: nowrap;    display: block;  }}:deep(.p-fileupload-buttonbar) {  background: transparent;  border: none;  padding: 0;}:deep(.p-fileupload-content) {  display: none;}:deep(.p-fileupload .p-button) {  background: transparent;  border: 2px solid #ff9800;  color: #ff9800;  width: 100%;  transition: all 0.3s ease;}:deep(.p-fileupload .p-button:hover) {  background: #ff9800;  color: white;  border-color: #ff9800;}:deep(.p-fileupload .p-button:focus) {  box-shadow: 0 0 0 0.2rem rgba(255, 152, 0, 0.25);}:deep(.p-checkbox) {  margin-right: 0.5rem;}/* Стили для select элементов */select {  padding: 0.75rem;  border: 1px solid #ced4da;  border-radius: 6px;  font-size: 1rem;  background-color: white;  color: #495057;  transition: all 0.2s ease;}select:focus {  outline: none;  border-color: #ff9800;  box-shadow: 0 0 0 0.2rem rgba(255, 152, 0, 0.25);}select.p-invalid {  border-color: #e24c4c;}select.p-invalid:focus {  border-color: #e24c4c;  box-shadow: 0 0 0 0.2rem rgba(226, 76, 76, 0.25);}/* Кастомный скроллбар */.register-content::-webkit-scrollbar {  width: 8px;}.register-content::-webkit-scrollbar-track {  background: #f1f1f1;  border-radius: 4px;  margin: 8px 0;}.register-content::-webkit-scrollbar-thumb {  background: #ff9800;  border-radius: 4px;  transition: background 0.3s ease;}.register-content::-webkit-scrollbar-thumb:hover {  background: #f57c00;}/* Общие мобильные стили */@media (max-width: 768px) {  .register-container {    padding: 1rem;  }  .register-card {    margin: 0;    border-radius: 12px;  }  .register-header {    padding: 1.5rem 1.5rem 1rem 1.5rem;  }  .register-content {    padding: 1rem 1.5rem 1.5rem 1.5rem;  }  .register-title {    font-size: 1.5rem;  }  .step-title {    font-size: 1.25rem;  }  .field {    margin-bottom: 1.25rem;  }  .field-label {    font-size: 0.85rem;    margin-bottom: 0.5rem;  }  /* Кнопки на всю ширину */  .p-button {    width: 100%;    justify-content: center;    padding: 0.875rem 1rem;    font-size: 1rem;  }  /* Поля ввода */  .p-inputtext,  .p-password-input,  select {    padding: 0.875rem;    font-size: 1rem;  }  /* Диалоги на мобильных */  :deep(.p-dialog) {    width: 95vw !important;    margin: 1rem;  }}</style>